
declare module '*.svg' {
  const src: string;
  export default src;
}
declare module '*.png' {
  const src: string;
  export default src;
}
declare module '*.jpg' {
  const src: string;
  export default src;
}
declare module '*.css?inline' {
  const content: string;
  export default content;
}
declare module '*.scss?inline' {
  const content: string;
  export default content;
}
